var vm=new Vue({el:"#app",data:{searchFilter:"",coinsList:[],page:{pageSize:8,currentPage:1,showItem:6,totalPage:0}},methods:{getCoinsList(){axios.get(window.reqUrl+"/get/coins/list",{params:{offset:this.page.currentPage,limit:this.page.pageSize,name:this.searchFilter}}).then(t=>{0==t.data.error?(this.coinsList=t.data.data.items,this.page.totalPage=t.data.data.count):alert(t.data.message)}).catch(function(t){console.log(t)})},getCoinDetail(t){localStorage.getItem("token")?location.href="coinDetail.html?coin_id="+t:location.href="login.html"},goto(t){t!=this.page.currentPage&&(this.page.currentPage=t,this.getCoinsList())},getColors(t){for(var e=[],a=0;a<t.length;a++){var i=parseFloat(t[a].hour_24_history)>=0?"#20ae16":"red";e.push(i)}return e}},created(){this.getCoinsList()},computed:{hours_1_index(){for(var t=[],e=0;e<this.coinsList.length;e++){var a=parseFloat(this.coinsList[e].hours_1_index)>=0?"#20ae16":"red";t.push(a)}return t},hours_24_index(){for(var t=[],e=0;e<this.coinsList.length;e++){var a=parseFloat(this.coinsList[e].hours_24_index)>=0?"#20ae16":"red";t.push(a)}return t},day_7_index(){for(var t=[],e=0;e<this.coinsList.length;e++){var a=parseFloat(this.coinsList[e].day_7_index)>=0?"#20ae16":"red";t.push(a)}return t}}});